import React, { useState } from 'react';
import { downloadDocumentAsPDF, downloadAsWord, downloadAsHTML, downloadAsText } from '../services/downloadService';

const DownloadDropdown = ({ document }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleDownload = async (format) => {
    try {
      switch (format) {
        case 'pdf':
          await downloadDocumentAsPDF(document);
          break;
        case 'word':
          await downloadAsWord(document);
          break;
        case 'html':
          await downloadAsHTML(document);
          break;
        case 'text':
          await downloadAsText(document);
          break;
      }
      setIsOpen(false);
    } catch (error) {
      console.error('Download failed:', error);
      alert('Download failed. Please try again.');
    }
  };

  return (
    <div className="relative">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
      >
        üì• Download
        <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
          <path fillRule="evenodd" d={isOpen ? 
            "M14.707 12.707a1 1 0 01-1.414 0L10 9.414l-3.293 3.293a1 1 0 01-1.414-1.414l4-4a1 1 0 011.414 0l4 4a1 1 0 010 1.414z" : 
            "M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
          } clipRule="evenodd" />
        </svg>
      </button>

      {isOpen && (
        <>
          <div 
            className="fixed inset-0 z-10" 
            onClick={() => setIsOpen(false)}
          />
          <div className="absolute right-0 top-full mt-2 w-48 bg-white border border-gray-200 rounded-lg shadow-lg z-20">
            <div className="py-1">
              <button 
                onClick={() => handleDownload('pdf')}
                className="w-full px-4 py-2 text-left hover:bg-gray-100 flex items-center gap-2 text-gray-700"
              >
                üìÑ Download as PDF
              </button>
              <button 
                onClick={() => handleDownload('word')}
                className="w-full px-4 py-2 text-left hover:bg-gray-100 flex items-center gap-2 text-gray-700"
              >
                üìù Download as Word
              </button>
              <button 
                onClick={() => handleDownload('html')}
                className="w-full px-4 py-2 text-left hover:bg-gray-100 flex items-center gap-2 text-gray-700"
              >
                üåê Download as HTML
              </button>
              <button 
                onClick={() => handleDownload('text')}
                className="w-full px-4 py-2 text-left hover:bg-gray-100 flex items-center gap-2 text-gray-700"
              >
                üìã Download as Text
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default DownloadDropdown;
