import React, { useState } from 'react';

const DownloadDropdown = ({ document }) => {
  const [isOpen, setIsOpen] = useState(false);

  console.log('DownloadDropdown rendered, isOpen:', isOpen); // Debug log

  const handleClick = () => {
    console.log('Button clicked! Current isOpen:', isOpen);
    setIsOpen(!isOpen);
    console.log('Setting isOpen to:', !isOpen);
  };

  return (
    <div className="relative inline-block">
      {/* Main Download Button */}
      <button
        onClick={handleClick}
        className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
      >
        üì• Download
        <span className="text-xs">{isOpen ? '‚ñ≤' : '‚ñº'}</span>
      </button>

      {/* Debug info */}
      <div className="text-xs text-red-500 mt-1">
        Debug: isOpen = {isOpen ? 'true' : 'false'}
      </div>

      {/* Dropdown menu */}
      {isOpen && (
        <div 
          className="absolute right-0 top-full mt-2 w-48 bg-white border border-gray-200 rounded-lg shadow-lg z-50"
        >
          <div className="p-2">
            <button 
              onClick={() => {
                console.log('PDF clicked');
                setIsOpen(false);
              }}
              className="w-full px-3 py-2 text-left hover:bg-gray-100 rounded flex items-center gap-2"
            >
              üìÑ Download as PDF
            </button>
            <button 
              onClick={() => {
                console.log('Word clicked');
                setIsOpen(false);
              }}
              className="w-full px-3 py-2 text-left hover:bg-gray-100 rounded flex items-center gap-2"
            >
              üìù Download as Word
            </button>
            <button 
              onClick={() => {
                console.log('HTML clicked');
                setIsOpen(false);
              }}
              className="w-full px-3 py-2 text-left hover:bg-gray-100 rounded flex items-center gap-2"
            >
              üåê Download as HTML
            </button>
            <button 
              onClick={() => {
                console.log('Text clicked');
                setIsOpen(false);
              }}
              className="w-full px-3 py-2 text-left hover:bg-gray-100 rounded flex items-center gap-2"
            >
              üìã Download as Text
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default DownloadDropdown;
