import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import  useAuth  from '../hooks/useAuth';
import useSocket from '../hooks/useSocket';

const CollaborationTest = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const [testMessage, setTestMessage] = useState('');
  const [receivedMessages, setReceivedMessages] = useState([]);
  const [connectedUsers, setConnectedUsers] = useState([]);
  const [socketStatus, setSocketStatus] = useState('Disconnected');

  const {
    emitDocumentChange,
    onDocumentChange,
    onUserJoined,
    onUserLeft,
    onUsersInDocument,
  } = useSocket(id, user?.id || user?._id, user?.name || user?.username || user?.email || 'Anonymous');

  useEffect(() => {
    if (!user) return;

    // Test document change
    onDocumentChange(({ content, userId, userName }) => {
      console.log('Received document change:', { content, userId, userName });
      setReceivedMessages(prev => [...prev, `Document changed by ${userName}: ${JSON.stringify(content).substring(0, 50)}...`]);
    });

    // Test user presence
    onUserJoined(({ userId, userName }) => {
      console.log('User joined:', { userId, userName });
      setConnectedUsers(prev => [...prev, { userId, userName }]);
      setReceivedMessages(prev => [...prev, `${userName} joined the document`]);
    });

    onUserLeft(({ userId, userName }) => {
      console.log('User left:', { userId, userName });
      setConnectedUsers(prev => prev.filter(u => u.userId !== userId));
      setReceivedMessages(prev => [...prev, `${userName} left the document`]);
    });

    onUsersInDocument((users) => {
      console.log('Users in document:', users);
      setConnectedUsers(users.filter(u => u.userId !== (user?.id || user?._id)));
    });

  }, [user, onDocumentChange, onUserJoined, onUserLeft, onUsersInDocument]);

  const sendTestMessage = () => {
    if (testMessage.trim()) {
      emitDocumentChange(null, [{ type: 'text', content: testMessage }]);
      setTestMessage('');
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Collaboration Test</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Connection Status */}
        <div className="bg-blue-50 p-4 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Connection Status</h2>
          <p>Socket Status: <span className="font-mono">{socketStatus}</span></p>
          <p>User: <span className="font-mono">{user?.name || user?.username || user?.email || 'Anonymous'}</span></p>
          <p>Document ID: <span className="font-mono">{id}</span></p>
        </div>

        {/* Connected Users */}
        <div className="bg-green-50 p-4 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Connected Users ({connectedUsers.length})</h2>
          {connectedUsers.length === 0 ? (
            <p className="text-gray-500">No other users connected</p>
          ) : (
            <ul>
              {connectedUsers.map((user, index) => (
                <li key={index} className="text-sm">• {user.userName}</li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* Test Message Sender */}
      <div className="mt-6 bg-yellow-50 p-4 rounded-lg">
        <h2 className="text-lg font-semibold mb-2">Send Test Message</h2>
        <div className="flex gap-2">
          <input
            type="text"
            value={testMessage}
            onChange={(e) => setTestMessage(e.target.value)}
            placeholder="Type a test message..."
            className="flex-1 px-3 py-2 border rounded"
            onKeyPress={(e) => e.key === 'Enter' && sendTestMessage()}
          />
          <button
            onClick={sendTestMessage}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Send
          </button>
        </div>
      </div>

      {/* Received Messages */}
      <div className="mt-6 bg-gray-50 p-4 rounded-lg">
        <h2 className="text-lg font-semibold mb-2">Received Messages</h2>
        <div className="max-h-64 overflow-y-auto">
          {receivedMessages.length === 0 ? (
            <p className="text-gray-500">No messages received yet</p>
          ) : (
            <ul className="space-y-1">
              {receivedMessages.map((message, index) => (
                <li key={index} className="text-sm bg-white p-2 rounded border">
                  {message}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* Instructions */}
      <div className="mt-6 bg-purple-50 p-4 rounded-lg">
        <h2 className="text-lg font-semibold mb-2">Testing Instructions</h2>
        <ol className="list-decimal list-inside space-y-1 text-sm">
          <li>Open this same URL in another browser tab/window</li>
          <li>Log in with a different account (or use incognito mode)</li>
          <li>Send a test message from one tab</li>
          <li>Watch for the message to appear in the other tab</li>
          <li>Check the "Connected Users" section to see other users</li>
        </ol>
      </div>
    </div>
  );
};

export default CollaborationTest;
