import axios from '../axiosInstance.js';

const API_URL = '/api/documents';

export const createDocument = async (documentData) => {
    const response = await axios.post(API_URL, documentData);
    return response.data;
};

export const getDocument = async (documentId) => {
    const response = await axios.get(`${API_URL}/${documentId}`);
    return response.data;
};

export const updateDocument = async (documentId, documentData) => {
    const response = await axios.put(`${API_URL}/${documentId}`, documentData);
    return response.data;
};

export const deleteDocument = async (documentId) => {
    const response = await axios.delete(`${API_URL}/${documentId}`);
    return response.data;
};

export const getAllDocuments = async () => {
    const response = await axios.get(API_URL);
    return response.data;
};

// Get document without authentication (for shared documents)
export const getSharedDocument = async (documentId) => {
    const response = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/documents/${documentId}`, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        },
    });

    if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch document');
    }

    return await response.json();
};
