import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import documentService from '../../services/documentService';

const DocumentsListPage = () => {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch all documents on component mount
  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const data = await documentService.getAllDocuments();
        setDocuments(data);
      } catch (err) {
        setError('Failed to fetch documents');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchDocuments();
  }, []);

  // Create new document and redirect to editor
  const handleCreateDocument = async () => {
    try {
      const newDoc = await documentService.createDocument({
        title: 'Untitled Document',
        content: '',
      });
      navigate(`/document/${newDoc._id}`);
    } catch (err) {
      console.error('Error creating document:', err);
    }
  };

  return (
    <div className="min-h-screen p-6 bg-gray-100 text-gray-800">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold mb-6 text-center">My Documents</h1>

        <div className="flex justify-end mb-6">
          <button
            onClick={handleCreateDocument}
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded"
          >
            + Create New Document
          </button>
        </div>

        {loading ? (
          <p>Loading documents...</p>
        ) : error ? (
          <p className="text-red-600">{error}</p>
        ) : documents.length === 0 ? (
          <p>No documents found. Create one now!</p>
        ) : (
          <ul className="space-y-4">
            {documents.map((doc) => (
              <li
                key={doc._id}
                onClick={() => navigate(`/document/${doc._id}`)}
                className="cursor-pointer bg-white hover:shadow-lg transition p-4 rounded shadow-sm border border-gray-200"
              >
                <h2 className="text-lg font-semibold">{doc.title || 'Untitled Document'}</h2>
                <p className="text-sm text-gray-500">Last updated: {new Date(doc.updatedAt).toLocaleString()}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default DocumentsListPage;
