import React, { useEffect, useState } from 'react';
import {
  getAllDocuments,
  createDocument,
  updateDocument,
  deleteDocument,
} from '../services/documentService';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';
import ThemeToggle from '../components/ThemeToggle';
import CreateDocumentForm from '../components/CreateDocumentForm';
import EditDocumentForm from '../components/EditDocumentForm';
import DocumentPreview from '../components/DocumentPreview';
import DocumentMeta from '../components/DocumentMeta';

const DocumentListPage = () => {
  const [documents, setDocuments] = useState([]);
  const [editDocId, setEditDocId] = useState(null);
  const { isDark } = useTheme();

  const navigate = useNavigate();

  // Fetch all documents
  const fetchDocuments = async () => {
    try {
      const data = await getAllDocuments();
      setDocuments(data);
    } catch (error) {
      console.error('Error fetching documents:', error);
    }
  };

  useEffect(() => {
    fetchDocuments();
  }, []);

  // Create a new document
  const handleCreate = async (documentData) => {
    try {
      await createDocument(documentData);
      fetchDocuments();
    } catch (error) {
      console.error('Error creating document:', error);
    }
  };

  // Delete a document
  const handleDelete = async (id) => {
    try {
      await deleteDocument(id);
      fetchDocuments();
    } catch (error) {
      console.error('Error deleting document:', error);
    }
  };

  // Start editing
  const startEditing = (doc) => {
    setEditDocId(doc._id);
  };

  // Save edited document
  const handleEditSave = async (updatedDocument) => {
    try {
      await updateDocument(editDocId, updatedDocument);
      setEditDocId(null);
      fetchDocuments();
    } catch (error) {
      console.error('Error updating document:', error);
    }
  };

  // Cancel editing
  const handleEditCancel = () => {
    setEditDocId(null);
  };

  return (
    <div className={`min-h-screen ${isDark ? 'bg-gray-900 text-white' : 'bg-gray-50'}`}>
      {/* Header */}
      <header className={`flex justify-between items-center p-6 border-b ${isDark ? 'border-gray-700 bg-gray-800' : 'border-gray-200 bg-white'}`}>
        <h1 className={`text-2xl font-bold ${isDark ? 'text-white' : 'text-gray-800'}`}>
          Your Documents
        </h1>
        <ThemeToggle />
      </header>

      <div className="p-6 max-w-3xl mx-auto">
        {/* Create New Document */}
        <CreateDocumentForm onSubmit={handleCreate} />

        {/* Document List */}
        {documents.length === 0 ? (
          <p className={`text-center mt-8 ${isDark ? 'text-gray-300' : 'text-gray-600'}`}>No documents found.</p>
        ) : (
          <div className="mt-8">
            <h2 className={`text-xl font-semibold mb-4 ${isDark ? 'text-white' : 'text-gray-800'}`}>
              Your Documents ({documents.length})
            </h2>
            <ul className="space-y-4">
              {documents.map((doc) => (
                <li key={doc._id} className={`border p-4 rounded ${isDark ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-300'}`}>
                  {editDocId === doc._id ? (
                    <EditDocumentForm
                      document={doc}
                      onSave={handleEditSave}
                      onCancel={handleEditCancel}
                    />
                  ) : (
                    <>
                      <h4 className={`text-xl font-semibold mb-2 ${isDark ? 'text-white' : 'text-gray-800'}`}>
                        {doc.title}
                      </h4>
                      
                      <DocumentPreview content={doc.content} maxLength={150} />
                      
                      <DocumentMeta document={doc} />

                      <div className="flex gap-2 mt-4">
                        <button
                          onClick={() => startEditing(doc)}
                          className="bg-yellow-400 text-white px-3 py-1 rounded hover:bg-yellow-500 transition-colors duration-200"
                        >
                          Quick Edit
                        </button>
                        <button
                          onClick={() => navigate(`/document/${doc._id}`)}
                          className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600 transition-colors duration-200"
                        >
                          Full Edit
                        </button>
                        <button
                          onClick={() => handleDelete(doc._id)}
                          className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 transition-colors duration-200"
                        >
                          Delete
                        </button>
                      </div>
                    </>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default DocumentListPage;
