import React, { useState, useRef, useEffect } from 'react';
import { downloadDocumentAsPDF, downloadAsWord, downloadAsHTML, downloadAsText } from '../services/downloadService';

const DownloadDropdown = ({ document }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [isDownloading, setIsDownloading] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const downloadOptions = [
    { 
      format: 'PDF', 
      icon: 'ðŸ“„', 
      description: 'Portable Document Format',
      handler: () => downloadDocumentAsPDF(document) 
    },
    { 
      format: 'Word (.docx)', 
      icon: 'ðŸ“', 
      description: 'Microsoft Word Document',
      handler: () => downloadAsWord(document) 
    },
    { 
      format: 'HTML', 
      icon: 'ðŸŒ', 
      description: 'Web Page',
      handler: () => downloadAsHTML(document) 
    },
    { 
      format: 'Plain Text', 
      icon: 'ðŸ“‹', 
      description: 'Text File',
      handler: () => downloadAsText(document) 
    }
  ];

  const handleDownload = async (option) => {
    try {
      setIsDownloading(true);
      console.log('Downloading:', option.format); // Debug log
      await option.handler();
      setIsOpen(false);
    } catch (error) {
      console.error('Download failed:', error);
      alert(`Failed to download ${option.format}`);
    } finally {
      setIsDownloading(false);
    }
  };

  const toggleDropdown = () => {
    console.log('Toggle dropdown, current state:', isOpen); // Debug log
    setIsOpen(!isOpen);
  };

  return (
    <div className="relative" ref={dropdownRef}>
      <button
        onClick={toggleDropdown}
        disabled={isDownloading}
        className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
      >
        <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
          <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm3.293-7.707a1 1 0 011.414 0L9 10.586V3a1 1 0 112 0v7.586l1.293-1.293a1 1 0 111.414 1.414l-3 3a1 1 0 01-1.414 0l-3-3a1 1 0 010-1.414z" clipRule="evenodd" />
        </svg>
        {isDownloading ? 'Downloading...' : 'Download'}
        <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
          <path fillRule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clipRule="evenodd" />
        </svg>
      </button>

      {/* Debug: Show dropdown state */}
      {console.log('Dropdown isOpen:', isOpen)}

      {isOpen && (
        <div className="absolute right-0 mt-2 w-64 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-50">
          <div className="p-2">
            <div className="text-xs text-gray-500 dark:text-gray-400 px-3 py-2 border-b border-gray-200 dark:border-gray-700">
              Choose download format
            </div>
            {downloadOptions.map((option) => (
              <button
                key={option.format}
                onClick={() => handleDownload(option)}
                disabled={isDownloading}
                className="w-full flex items-center gap-3 px-3 py-3 text-left hover:bg-gray-50 dark:hover:bg-gray-700 rounded-md transition-colors disabled:opacity-50"
              >
                <span className="text-lg">{option.icon}</span>
                <div className="flex-1">
                  <div className="text-sm font-medium text-gray-900 dark:text-white">
                    {option.format}
                  </div>
                  <div className="text-xs text-gray-500 dark:text-gray-400">
                    {option.description}
                  </div>
                </div>
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default DownloadDropdown;
