import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getDocument, updateDocument } from '../services/documentService';
import TextElement from './TextElement';
import ImageElement from './ImageElement';
import { useNavigate } from 'react-router-dom';

const DocumentEditor = () => {
  const { id } = useParams();
  const [document, setDocument] = useState({ title: '', content: [] });
  const [isLoading, setIsLoading] = useState(true);

  const navigate = useNavigate();

const handleChange = (updatedContent) => {
  setDocument((prevDoc) => ({ ...prevDoc, content: updatedContent }));
};


  useEffect(() => {
    const fetchDocument = async () => {
      try {
        const fetched = await getDocument(id);
        console.log('Fetched document on edit page:', fetched);
        const contentArray = Array.isArray(fetched.content)
          ? fetched.content
          : [{ type: 'text', content: fetched.content || '' }];

        setDocument({
          title: fetched.title || 'Untitled Document',
          content: contentArray,
        });
      } catch (error) {
        console.error('Error fetching document:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchDocument();
  }, [id]);


  const handleSave = async () => {
    try {
      await updateDocument(id, document);
      alert('Document saved successfully!');
    } catch (error) {
      console.error('Error saving document:', error);
      alert('Failed to save document.');
    }
  };


  const handleAddImage = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      const imageBlock = {
        type: 'image',
        src: reader.result, // base64
        alt: file.name,
      };

      setDocument((prevDoc) => ({
        ...prevDoc,
        content: [...prevDoc.content, imageBlock],
      }));
    };

    reader.readAsDataURL(file);
  };


  if (isLoading) return <div className="p-4">Loading...</div>;

  return (
    <div className="p-4">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">{document.title || 'Untitled Document'}</h1>
        <div className="space-x-2">
          <button
            onClick={() => navigate('/documents')}
            className="bg-gray-300 text-black px-3 py-1 rounded hover:bg-gray-400"
          >
            Back to Documents
          </button>
          <button
            onClick={handleSave}
            className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
          >
            Save Document
          </button>
        </div>
      </div>

      {/* Editor content below */}
      <div>
        {document.content.map((element, index) => {
          if (element.type === 'text') {
            return (
              <TextElement
                key={index}
                content={element.content}
                onChange={(newContent) => {
                  const updatedContent = [...document.content];
                  updatedContent[index].content = newContent;
                  handleChange(updatedContent);
                }}
              />
            );
          } else if (element.type === 'image') {
            return (
              <ImageElement
                key={index}
                src={element.src}
                alt={element.alt}
                onDelete={() => {
                  const updatedContent = document.content.filter((_, i) => i !== index);
                  handleChange(updatedContent);
                }}
              />
            );
          }
          return null;
        })}
      </div>
    </div>
  );
};

export default DocumentEditor;
